def encode(text):B={'a':'ᔑ','b':'ܠ','c':'i','d':'↸','e':'ᒷ','f':'⎓','g':'├','h':'₸','i':'╎','j':'⋮','k':'ꖌ','l':'|:','m':'٦','n':'リ','o':'フ','p':'¡ǃ','q':'ᑖ','r':'∴','s':'߆','t':'ℸ ̣','u':'⚍','v':'⍊','w':'∷','x':'˙̸ ','y':'॥','z':'⋂','1':'⥍','2':'∠','3':'>','4':'⊐','5':'ⵎ','6':'X','7':'Δ','8':'⎕','9':'┌┐','0':'└┘'};return''.join(B.get(A,A)for A in text)
def decode(text):B={'ᔑ':'a','ܠ':'b','i':'c','↸':'d','ᒷ':'e','⎓':'f','├':'g','₸':'h','╎':'i','⋮':'j','ꖌ':'k','|:':'l','٦':'m','リ':'n','フ':'o','¡ǃ':'p','ᑖ':'q','∴':'r','߆':'s','ℸ ̣':'t','⚍':'u','⍊':'v','∷':'w','˙̸ ':'x','॥':'y','⋂':'z','⥍':'1','∠':'2','>':'3','⊐':'4','ⵎ':'5','X':'6','Δ':'7','⎕':'8','┌┐':'9','└┘':'0'};return''.join(B.get(A,A)for A in text)